package cn.edu.ouc.pojo.vo;
/**
 * 
 * @ClassName: Select2.java
 * @Description: 公共的名字类，用于前台显示格式为{"id":"","text":""}格式的数据
 *
 * @version: v1.0.0
 * 
 */
public class Select2 {

	public Select2() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Select2(Integer id, String text) {
		super();
		this.id = id;
		this.text = text;
	}
	private Integer id;
	private String text;
	public Integer getId() {
		return id;
	}
	public void setId(Integer id) {
		this.id = id;
	}
	public String getText() {
		return text;
	}
	public void setText(String text) {
		this.text = text;
	}
	@Override
	public String toString() {
		return "Select2 [id=" + id + ", text=" + text + "]";
	}
}
